export type WriteOptions = {
    path?: string | number;
    value?: any;
    object?: Parseable;
}
export type ReadOptions = {
    path?: string | number;
    alternative?: any;
    object?: Parseable;
}

export type Options = {
    skip?: SkipList;
    functions?: boolean;
    paths?: {
        full?: boolean;
        test?: (path: string) => any;
    };
}

export type SkipList = Array<any> | false

export type Parseable = any;
